        const currentChecked = checked.indexOf(c)

        const newChecked = [...checked]
        if(currentChecked === -1){
            newChecked.push(c)
        }
        else{
            newChecked.splice(currentChecked,1)
        }        
        setChecked(newChecked)
        handleFilters(newChecked)
    }

    return(
        categories.map((c,i)=>{
            return(
