import React,{useState,useEffect} from 'react'
import Layout from './Layout'
import {getCategories,list} from './apiCore'
import Card from './Card'
import { getitemsFromCart } from './cartHelpers'
import { Link } from 'react-router-dom'
import Checkout from './Checkout'

const Cart = () =>{

    const [cartItems,setCartItems] = useState([])

    useEffect(()=>{

        
        
        setCartItems(()=>getitemsFromCart())
        
    },[cartItems])

    const getItems = () =>{
        const items = getitemsFromCart()
        
        //setItems(items=>[...items])
    }    

    const showItems = items =>{
        return(
            <div>
                <h2>Your cart has {items.length} items</h2>
                <hr/>
                {cartItems.map((item,index)=>{
                return(
                    <Card key={index} product={item} showAddToCartButton={false} update={true} removeItemButton={true}></Card>
                )
            })}
            </div>
        )
    }

    const noItemsInCart = () =>{
        return(
            <h2>Your cart is empty. <Link to='/shop'>Continue Shopping</Link></h2>
        )
    }

    return(
        <Layout title="Cart page" description="Manage cart items. Add or remove or continue to checkout" className="container-fluid">
           <div className='row'>
               <div className='col-6'>
                    {cartItems.length > 0 ? showItems(cartItems) : noItemsInCart()}
               </div>
               <div className='col-6'>
                    <Checkout products={items}/>
               </div>
           </div>
            
        </Layout>
    )

}

export default Cart
