import React,{useState,useEffect} from 'react'
import Layout from './Layout'
import {getCategories,list} from './apiCore'
import Card from './Card'


const Search = () =>{

    const [data,setData] = useState({
        categories: [],
        category: '',
        search: '',
        results: [],
        searched: false
    })
    const [error, setError] = useState(false);
    const {categories,category,results,searched,search} = data
    

    const loadCategories = () =>{
        getCategories().then(data =>{
            if(data.error){
                setError(data.error)
            }
            else{
                setData({...data,categories:data})
            }
        })
    }

    useEffect(()=>{
        loadCategories()
    },[])

    const searchData = () =>{
        if(search){
            list({search:search || undefined, category:category})
            .then(data=>{
                if(data.error){
                    setError(data.error)
                }else{
                    setData({results:data,searched:true})
                }
            })
        }
    }

    const searchSubmit = (e) =>{
        e.preventDefault()
        searchData()
    }

    const handleChange = (name) =>(event) =>{
        setData({...data,[name]:event.target.value,searched:false})
    }

    const searchForm = () =>{
        return(
            <form onSubmit={searchSubmit}>
                <span className='input-group-text'>
                    <div className='input-group input-group-lg'>
                        <div className='input-group-prepend'>
                            <select className='mr-2 ' onChange={handleChange('category')}>
                                <option value='All'>Pick Category</option>
                                {categories.map((category,index)=>{
                                    return(
                                    <option key={index} value={category._id}>{category.name}</option>
                                    )
                                })}
                            </select>
                        </div>
                    <input type='search' className='form-control' onChange={handleChange('search')} placeholder='Search by name'></input>

                </div>
                <div className='btn input-group-append' style={{border:'none'}}>
                    <button className='input-group-text'>Search</button>
                </div>
                </span>
            </form>
        )
        
    }

    return (
        <div className='row'>
            <div className='container'>{searchForm()}</div>
            {JSON.stringify(results)}
        </div>
    )
}

export default Search
